    fun getQuestionsByCategory(categoryId: String, count: Int): List<Question> {
        val allQuestions = when (categoryId) {
            "medicine" -> medicineQuestions
            "dota2" -> dota2Questions
            "history" -> historyQuestions
            "science" -> scienceQuestions
            else -> QuestionsProvider.allQuestions // общий тест
        }

        return allQuestions.shuffled(Random(System.currentTimeMillis())).take(count)
    }
